  color: green;
  font-weight: bold;
}

/* Descendant combinator: targets any span inside a div */
div span {
  display: block;
  margin-bottom: 5px;
}

/* Child combinator: targets only direct span children of a section */
section > span {
  border-bottom: 1px dotted blue;
}

