```javascript
const canvas = document.getElementById('myCanvas');
const ctx = canvas.getContext('2d');

const img = new Image();
img.crossOrigin = 'Anonymous'; // Required for images from different origins
img.src = 'https://via.placeholder.com/200/FF0000/FFFFFF?text=Original'; // Example: Red square

img.onload = () => {
  // 3. Draw the original image onto the canvas
  ctx.drawImage(img, 0, 0);

  // 4. Get the image's pixel data
  const imageData = ctx.getImageData(0, 0, img.width, img.height);
  const data = imageData.data; // This is a Uint8ClampedArray

  // 5. Iterate and convert to grayscale
  for (let i = 0; i < data.length; i += 4) {
    const r = data[i];     // Red component
    const g = data[i + 1]; // Green component
    const b = data[i + 2]; // Blue component

    // Calculate grayscale value
    const gray = (r + g + b) / 3;

    // Set R, G, B to the grayscale value
    data[i] = gray;     // Red
    data[i + 1] = gray; // Green
    data[i + 2] = gray; // Blue
    // Alpha (data[i+3]) remains unchanged
  }

  // 6. Put the modified pixel data back onto the canvas
  ctx.putImageData(imageData, 0, 0);
};

// Error handling for image loading
img.onerror = () => {
  console.error('Failed to load image.');
};
```

