import React, { Suspense, lazy } from 'react';

// Assume these components exist in separate files:
// export default function HomePage() { return <h2>Home Page</h2>; }
// export default function AboutPage() { return <h2>About Page</h2>; }

const HomePage = lazy(() => import('./HomePage'));
const AboutPage = lazy(() => import('./AboutPage'));

const App = () => {
  // Simulate a simple routing mechanism
  const path = window.location.pathname;

  let ComponentToRender = null;
  if (path === '/') {
    ComponentToRender = HomePage;
  } else if (path === '/about') {
    ComponentToRender = AboutPage;
  }

  return (
    <div>
      <h1>My App</h1>
      <nav>
        <a href="/">Home</a> | <a href="/about">About</a>
      </nav>
      <Suspense fallback={<div>Loading...</div>}>
        {ComponentToRender ? <ComponentToRender /> : <div>Page not found</div>}
      </Suspense>
    </div>
  );
};

export default App;
```

