console.log(sum(2, 3)); // 'subtract' function will be removed by tree shaking in production build

const TerserPlugin = require('terser-webpack-plugin');

module.exports = {
  mode: 'production', // Enables production optimizations including tree shaking and minification
  optimization: {
    minimizer: [new TerserPlugin({ // Minifies JavaScript files
      terserOptions: {
