      minSize: 20000, // Minimum size of a chunk to be considered for splitting (in bytes)
      maxInitialRequests: 3, // Maximum number of parallel requests on initial load
      maxAsyncRequests: 5, // Maximum number of parallel requests for on-demand loading
      cacheGroups: {
        vendors: {
