          name: 'vendors',
          chunks: 'all',
        },
        // You can add custom cache groups here
      },
    },
  },
  // Other webpack configurations...
};

// Node.js Express example for setting Cache-Control headers
const express = require('express');
const app = express();

app.get('/static-asset.js', (req, res) => {
  // Cache for 1 year (31536000 seconds) and mark as immutable
  res.setHeader('Cache-Control', 'public, max-age=31536000, immutable');
  res.send('console.log("This asset is cached for a year!");');
});

app.get('/dynamic-data', (req, res) => {
  // Do not cache this response at all
  res.setHeader('Cache-Control', 'no-store, must-revalidate');
  res.setHeader('Pragma', 'no-cache'); // For HTTP/1.0 compatibility
  res.setHeader('Expires', '0'); // For HTTP/1.0 compatibility
  res.json({ message: 'This data is always fresh.' });
});

// Start server...
app.listen(3000, () => console.log('Server running on port 3000'));

// service-worker.js
const CACHE_NAME = 'v1';
const urlsToCache = [
  '/',
  '/index.html',
  '/styles.css',
  '/script.js'
];

// Install event: pre-cache static assets
self.addEventListener('install', event => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(cache => {
        console.log('Opened cache');
        return cache.addAll(urlsToCache);
      })
  );
});

// Fetch event: intercept network requests and serve from cache if available
self.addEventListener('fetch', event => {
  event.respondWith(
    caches.match(event.request).then(cachedResponse => {
      // Return cached response if available
      if (cachedResponse) {
        console.log('Serving from cache:', event.request.url);
        return cachedResponse;
      }

      // Otherwise, fetch from network
      return fetch(event.request).then(response => {
        // Check if we received a valid response
        if (!response || response.status !== 200 || response.type !== 'basic') {
          return response;
        }

        // Clone the response because it's a stream and can only be consumed once
        const responseToCache = response.clone();

        caches.open(CACHE_NAME).then(cache => {
          cache.put(event.request, responseToCache);
        });

        return response;
      });
    })
  );
});

// Activate event: clean up old caches
self.addEventListener('activate', event => {
  const cacheWhitelist = [CACHE_NAME];
  event.waitUntil(
    caches.keys().then(cacheNames => {
      return Promise.all(
        cacheNames.map(cacheName => {
          if (cacheWhitelist.indexOf(cacheName) === -1) {
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
});

import React, { useState, useCallback, useMemo } from 'react';

// Child component that is expensive to render or re-render
const ExpensiveChild = React.memo(({ data, onClick }) => {
  console.log('ExpensiveChild rendered', data);
  return (
    <div style={{ border: '1px solid blue', padding: '10px', margin: '10px' }}>
      <h3>Memoized Child Component</h3>
      <p>Data: {data}</p>
      <button onClick={onClick}>Click me</button>
    </div>
  );
});

// Parent Component
const ParentComponent = () => {
  const [count, setCount] = useState(0);
  const [text, setText] = useState('');

  // Memoize the callback function using useCallback
  // This function will only be recreated if `count` changes.
  const handleClick = useCallback(() => {
    setCount(prevCount => prevCount + 1);
  }, []); // Empty dependency array means it's created once

  // Memoize a value using useMemo
  // This value will only be recomputed if `count` changes.
  const processedData = useMemo(() => {
    console.log('Recalculating processedData...');
    return `Count: ${count * 2}`;
  }, [count]);

  return (
    <div style={{ border: '1px solid green', padding: '20px' }}>
      <h2>Parent Component</h2>
      <p>Parent Count: {count}</p>
      <input type="text" value={text} onChange={(e) => setText(e.target.value)} placeholder="Type something"/>

      {/* ExpensiveChild will only re-render if processedData or handleClick change their references */}
      <ExpensiveChild data={processedData} onClick={handleClick} />
    </div>
  );
};

export default ParentComponent;

