import React, { useState, useMemo, useCallback } from 'react';

// DataTable component wrapped with React.memo
const DataTable = React.memo(({ data, filterText, sortOrder, onFilterChange, onSortChange }) => {
  console.log('DataTable (component) rendered');

  // Memoize the filtering and sorting operation
  const filteredAndSortedData = useMemo(() => {
    console.log('Filtering and sorting data...'); // This should only log when data, filterText, or sortOrder changes
    let processedData = [...data];
    if (filterText) {
      processedData = processedData.filter(item => 
        item.name.toLowerCase().includes(filterText.toLowerCase())
      );
    }
    processedData.sort((a, b) => {
      if (sortOrder === 'asc') return a.name.localeCompare(b.name);
      if (sortOrder === 'desc') return b.name.localeCompare(a.name);
      return 0;
    });
    return processedData;
  }, [data, filterText, sortOrder]); // Dependencies for useMemo

  return (
    <div style={{ margin: '20px', border: '1px solid #ccc', padding: '10px' }}>
      <h2>Data Table ({filteredAndSortedData.length} items)</h2>
      <input 
        type="text" 
        placeholder="Filter by name..." 
        value={filterText}
        onChange={onFilterChange} // Use memoized callback from parent
      />
      <select value={sortOrder} onChange={onSortChange}> {/* Use memoized callback from parent */}
        <option value="">No Sort</option>
        <option value="asc">Name A-Z</option>
        <option value="desc">Name Z-A</option>
      </select>
      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '15px' }}>
        <thead>
          <tr style={{ background: '#f0f0f0' }}>
            <th style={{ padding: '8px', border: '1px solid #ddd', textAlign: 'left' }}>ID</th>
            <th style={{ padding: '8px', border: '1px solid #ddd', textAlign: 'left' }}>Name</th>
            <th style={{ padding: '8px', border: '1px solid #ddd', textAlign: 'left' }}>Category</th>
            <th style={{ padding: '8px', border: '1px solid #ddd', textAlign: 'left' }}>Price</th>
          </tr>
        </thead>
        <tbody>
          {filteredAndSortedData.map(item => (
            <tr key={item.id}>
              <td style={{ padding: '8px', border: '1px solid #ddd' }}>{item.id}</td>
              <td style={{ padding: '8px', border: '1px solid #ddd' }}>{item.name}</td>
              <td style={{ padding: '8px', border: '1px solid #ddd' }}>{item.category}</td>
              <td style={{ padding: '8px', border: '1px solid #ddd' }}>{item.price.toFixed(2)}</td>
            </tr>
          ))}
          {filteredAndSortedData.length === 0 && (
            <tr><td colSpan="4" style={{ textAlign: 'center', padding: '10px' }}>No items found</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
});

