import React, { useMemo, useCallback, useState } from 'react';

function ExpensiveCalculationDisplay({ numbers }) {
  const sum = useMemo(() => {
    console.log('Calculating sum...');
    return numbers.reduce((acc, num) => acc + num, 0);
  }, [numbers]); // Recalculates only if 'numbers' array changes

  return <p>Sum: {sum}</p>;
}

// Memoized child component for demonstration
const Button = React.memo(({ onClick, label }) => {
  console.log(`Rendering ${label} Button`);
  return <button onClick={onClick}>{label}</button>;
});

function ParentComponent() {
  const [count, setCount] = useState(0);
  const [data, setData] = useState([1, 2, 3]);

  // This function would be re-created on every render without useCallback
  // and cause Button to re-render even if its props haven't conceptually changed.
  const incrementCount = useCallback(() => {
    setCount(prevCount => prevCount + 1);
  }, []); // Empty dependency array means it's created once

  return (
    <div>
      <p>Count: {count}</p>
      <Button onClick={incrementCount} label="Increment" />
      <ExpensiveCalculationDisplay numbers={data} />
      <button onClick={() => setData([...data, Math.random()])}>Add Random Number</button>
      <button onClick={() => setCount(count + 1)}>Force Parent Re-render</button>
    </div>
  );
}

import React from 'react';

interface ItemProps {
  item: { id: number; value: string; timestamp: number; };
  onClick: (id: number) => void;
}

// Custom comparison function for React.memo
// Only re-render if id or value changes, ignore timestamp
const areEqual = (prevProps: ItemProps, nextProps: ItemProps) => {
  return (
    prevProps.item.id === nextProps.item.id &&
    prevProps.item.value === nextProps.item.value &&
    prevProps.onClick === nextProps.onClick // Ensure callback is stable
  );
};

const MemoizedListItem = React.memo(({
  item,
  onClick
}: ItemProps) => {
  console.log(`Rendering Item: ${item.value}`);
  return (
    <li onClick={() => onClick(item.id)}>
      {item.value} (ID: {item.id})
    </li>
  );
}, areEqual);

function ItemList({ items, onItemClick }) {
  return (
    <ul>
      {items.map(item => (
        <MemoizedListItem key={item.id} item={item} onClick={onItemClick} />
      ))}
    </ul>
  );
}

