class MyTextInput extends HTMLElement {
  private inputElement: HTMLInputElement;
  private labelElement: HTMLLabelElement;

  constructor() {
    super();
    const shadowRoot = this.attachShadow({ mode: 'open' });

    const wrapper = document.createElement('div');
    wrapper.style.padding = '10px';
    wrapper.style.border = '1px solid #ddd';
    wrapper.style.borderRadius = '5px';

    this.labelElement = document.createElement('label');
    this.labelElement.style.marginRight = '5px';
    this.labelElement.style.fontWeight = 'bold';

    this.inputElement = document.createElement('input');
    this.inputElement.type = 'text';
    this.inputElement.style.padding = '8px';
    this.inputElement.style.border = '1px solid #ccc';
    this.inputElement.style.borderRadius = '3px';

    this.inputElement.addEventListener('input', (e) => {
      const event = new CustomEvent('input-changed', {
        detail: { value: (e.target as HTMLInputElement).value },
        bubbles: true,
        composed: true
      });
      this.dispatchEvent(event);
    });

    wrapper.appendChild(this.labelElement);
    wrapper.appendChild(this.inputElement);
    shadowRoot.appendChild(wrapper);
  }

  static get observedAttributes() {
    return ['label'];
  }

  attributeChangedCallback(name: string, oldValue: string | null, newValue: string | null) {
    if (name === 'label') {
      this.labelElement.textContent = newValue ? newValue + ':' : '';
    }
  }
}

if (!customElements.get('my-text-input')) {
  customElements.define('my-text-input', MyTextInput);
}

// --- React Component
export default function App() {
  const [inputValue, setInputValue] = useState('');
  const inputRef = useRef<HTMLElement>(null); // Ref to hold the Web Component element

  useEffect(() => {
    const currentInput = inputRef.current;

    const handleInputChange = (event: Event) => {
      const customEvent = event as CustomEvent;
      setInputValue(customEvent.detail.value);
    };

    if (currentInput) {
      // Attach the native DOM event listener to the Web Component
      currentInput.addEventListener('input-changed', handleInputChange);
    }

    // Cleanup function to remove the event listener when component unmounts
    return () => {
      if (currentInput) {
        currentInput.removeEventListener('input-changed', handleInputChange);
      }
    };
  }, []); // Empty dependency array means this effect runs once on mount and cleans up on unmount

  return (
    <div>
      <h1>Web Component Integration</h1>
      <p>Value from Web Component: <strong>{inputValue}</strong></p>

      {/* Use the Web Component and pass the ref */}
      <my-text-input label="Your Name" ref={inputRef}></my-text-input>
    </div>
  );
}

