const gulp = require('gulp');
const sass = require('gulp-sass')(require('sass'));
const autoprefixer = require('gulp-autoprefixer');
const uglify = require('gulp-uglify'); // Added
const rename = require('gulp-rename'); // Added

// Compile SCSS to CSS
function compileSass() {
  return gulp.src('./src/scss/**/*.scss')
    .pipe(sass().on('error', sass.logError))
    .pipe(autoprefixer({
      overrideBrowserslist: ['last 2 versions'],
      cascade: false
    }))
    .pipe(gulp.dest('./dist/css'));
}

// Minify JavaScript
function minifyJs() {
  return gulp.src('./src/js/**/*.js')
    .pipe(uglify()) // Minify JS
    .pipe(rename({ suffix: '.min' })) // Add .min suffix
    .pipe(gulp.dest('./dist/js'));
}

// Copy HTML files
function copyHtml() {
  return gulp.src('./src/*.html')
    .pipe(gulp.dest('./dist'));
}

// Watch for changes
function watchFiles() {
  gulp.watch('./src/scss/**/*.scss', compileSass);
  gulp.watch('./src/js/**/*.js', minifyJs); // Watch JS
  gulp.watch('./src/*.html', copyHtml); // Watch HTML
}

// Export tasks
exports.sass = compileSass;
exports.minifyJs = minifyJs;
exports.copyHtml = copyHtml;
exports.watch = watchFiles;

// Define a 'build' task to run all compilation/copy tasks
const build = gulp.series(compileSass, minifyJs, copyHtml);

// Default task: run build then start watching
exports.default = gulp.series(build, watchFiles);

