import { gql } from '@apollo/client';

const MOCK_USER_DATA = {
  user: {
    id: '101',
    name: 'Alice Smith',
    email: 'alice@example.com',
    posts: [
      { id: 'p1', title: 'My First Blog' },
      { id: 'p2', title: 'GraphQL Basics' }
    ]
  }
};

const MOCK_POSTS_DATA = {
  posts: [
    { id: 'p1', title: 'Post One' },
    { id: 'p2', title: 'Post Two' },
  ]
};

class MockGraphQLClient {
  constructor(uri) {
    this.uri = uri;
  }

  query({ query, variables }) {
    console.log('Mock Query Received:', { query: query.loc.source.body.trim().split(/\s+/)[1], variables });
    return new Promise((resolve, reject) => {
      setTimeout(() => { // Simulate network delay
        if (query.loc.source.body.includes('GetUser')) {
          resolve({ data: MOCK_USER_DATA });
        } else if (query.loc.source.body.includes('GetPosts')) {
          resolve({ data: MOCK_POSTS_DATA });
        } else {
          reject(new Error('Unknown mock query operation.'));
        }
      }, 100);
    });
  }

  mutate({ mutation, variables }) {
    console.log('Mock Mutation Received:', { mutation: mutation.loc.source.body.trim().split(/\s+/)[1], variables });
    return new Promise((resolve, reject) => {
      setTimeout(() => { // Simulate network delay
        if (mutation.loc.source.body.includes('CreatePost')) {
          const newPostId = `p${Math.floor(Math.random() * 100000)}`;
          const createdPost = {
            id: newPostId,
            title: variables.title || 'Untitled Post',
            content: variables.content || 'No content provided',
            authorId: variables.authorId || 'unknown'
          };
          resolve({ data: { createPost: { id: createdPost.id, title: createdPost.title } } });
        } else {
          reject(new Error('Unknown mock mutation operation.'));
        }
      }, 100);
    });
  }
}

// Example Usage (for testing your implementation)
const client = new MockGraphQLClient('http://mock-graphql.com/graphql');

// Test Query
client.query({
  query: gql`query GetUser($id: ID!) { user(id: $id) { name } }`,
  variables: { id: '1' }
}).then(result => console.log('Query Result:', result.data))
  .catch(error => console.error('Query Error:', error));

client.query({
  query: gql`query GetPosts { posts { id title } }`,
  variables: {}
}).then(result => console.log('Query Result:', result.data))
