function getFizzBuzzResults(start, end) {
  const results = [];

  // Handle edge case: if start > end or invalid range, return empty array
  if (start > end || typeof start !== 'number' || typeof end !== 'number' || !Number.isInteger(start) || !Number.isInteger(end)) {
    return [];
  }

  for (let i = start; i <= end; i++) {
    if (i % 3 === 0 && i % 5 === 0) {
      results.push('FizzBuzz');
    } else if (i % 3 === 0) {
      results.push('Fizz');
    } else if (i % 5 === 0) {
      results.push('Buzz');
    } else {
      results.push(String(i)); // Ensure numbers are stored as strings
    }
  }

  return results;
}

