// Constructor function
function Person(name) {
  this.name = name;
}

// Add a method to the Person's prototype
Person.prototype.greet = function() {
  console.log(`Hello, my name is ${this.name}`);
};

const john = new Person('John');
john.greet(); // John inherits greet() from Person.prototype

console.log(john.__proto__ === Person.prototype); // true
console.log(Object.getPrototypeOf(john) === Person.prototype); // true

// Another object inheriting from Person.prototype
const jane = new Person('Jane');
jane.greet();

// Properties/methods not found on the instance are looked up on the prototype chain
console.log(john.hasOwnProperty('name')); // true (name is directly on john)
console.log(john.hasOwnProperty('greet')); // false (greet is on the prototype)

console.log('Start'); // 1. Synchronous code

setTimeout(() => { // 2. Macrotask (Callback Queue)
  console.log('Timeout 1');
}, 0);

Promise.resolve().then(() => { // 3. Microtask (Microtask Queue)
  console.log('Promise 1');
});

setTimeout(() => { // 4. Macrotask (Callback Queue)
  console.log('Timeout 2');
}, 0);

Promise.resolve().then(() => { // 5. Microtask (Microtask Queue)
  console.log('Promise 2');
});

console.log('End'); // 6. Synchronous code

// Expected Output:
// Start
// End
// Promise 1
// Promise 2
// Timeout 1
// Timeout 2

/* 
  Conceptual process: 
  1. Initial Render: 
     const vdom1 = React.render(<App />);
     ReactDOM.render(vdom1, document.getElementById('root'));

  2. State Update (e.g., click a button):
     setState({ count: this.state.count + 1 });

  3. Re-render Virtual DOM:
     const vdom2 = React.render(<App newProps />);

  4. Diffing:
     React compares vdom1 and vdom2 to find differences.

  5. Reconciliation (Actual DOM Update):
     ReactDOM.patch(document.getElementById('root'), diffs); 
     (Only apply the minimal changes to the real DOM)
*/

// No direct code example as Virtual DOM is an internal mechanism, 
// but this conceptual flow explains its role in a React component's life cycle.
function Counter() {
  const [count, setCount] = React.useState(0);

  const increment = () => {
    setCount(count + 1); // This triggers a Virtual DOM update internally
  };

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={increment}>Increment</button>
    </div>
  );
}

function ParentComponent() {
  // State managed by ParentComponent
  const [message, setMessage] = React.useState('Hello from Parent!');

  const handleButtonClick = () => {
    setMessage('Message updated by Parent!');
  };

  return (
    <div>
      <ChildComponent greeting={message} /> {/* message passed as prop */}
      <button onClick={handleButtonClick}>Update Message</button>
    </div>
  );
}

function ChildComponent(props) {
  // props.greeting is read-only here
  // props.greeting = 'New value'; // ERROR: Props are immutable

  // State managed internally by ChildComponent
  const [childClicks, setChildClicks] = React.useState(0);

  const handleChildClick = () => {
    setChildClicks(childClicks + 1);
  };

  return (
    <div>
      <p>Parent says: {props.greeting}</p>
      <p>Child has been clicked: {childClicks} times</p>
      <button onClick={handleChildClick}>Click Child</button>
    </div>
  );
}

import React, { useState, useEffect } from 'react';

function Timer() {
  // useState: add state to functional component
  const [seconds, setSeconds] = useState(0);

  // useEffect: run side effects after render
  useEffect(() => {
    // This function runs once after initial render and on subsequent updates
    const intervalId = setInterval(() => {
      setSeconds(prevSeconds => prevSeconds + 1);
    }, 1000);

    // Cleanup function: runs when component unmounts or before re-running effect
    return () => clearInterval(intervalId);
  }, []); // Empty dependency array means effect runs once on mount and cleans up on unmount

  return (
    <div>
      <p>Seconds: {seconds}</p>
    </div>
  );
}

class MyCounter extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
    this.handleClick = this.handleClick.bind(this);
    this.handleAsyncClick = this.handleAsyncClick.bind(this);
  }

  handleClick() {
    // Async nature: console.log might show old state if batched
    this.setState({ count: this.state.count + 1 });
    console.log('After setState (potentially stale):', this.state.count); 
    // ^ This will often log the value *before* the update

    // Using callback to ensure code runs after update
    this.setState({ count: this.state.count + 1 }, () => {
      console.log('After setState (callback ensures updated):', this.state.count);
    });

    // Functional setState: recommended for updates depending on previous state
    this.setState(prevState => ({
      count: prevState.count + 1
    }));
  }

  // Example where functional setState is critical
  handleAsyncClick() {
    // These two calls are likely batched, but functional form ensures correct increments
    setTimeout(() => {
      this.setState(prevState => ({ count: prevState.count + 1 }));
      this.setState(prevState => ({ count: prevState.count + 1 }));
      console.log('Async update complete:', this.state.count); // This will log the final batched value
    }, 0);
  }

  render() {
    return (
      <div>
        <p>Count: {this.state.count}</p>
        <button onClick={this.handleClick}>Increment Sync</button>
        <button onClick={this.handleAsyncClick}>Increment Async</button>
      </div>
    );
  }
}

