import React, { Component } from 'react';

class AsyncCounter extends Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }

  handleSyncClick = () => {
    // First setState: demonstrates asynchronous nature
    this.setState({ count: this.state.count + 1 });
    console.log('Sync Click - After 1st setState (before update):', this.state.count);

    // Second setState: functional form with callback for guaranteed update
    this.setState((prevState) => ({
      count: prevState.count + 1
    }), () => {
      console.log('Sync Click - After 2nd setState (callback):', this.state.count);
      console.log('--- Sync Click batch complete ---');
    });
  };

  incrementTwiceAsync = () => {
    setTimeout(() => {
      // Using functional setState in an asynchronous context (like setTimeout)
      // ensures correct updates even when React's internal batching is bypassed.
      this.setState(prevState => ({ count: prevState.count + 1 }));
      this.setState(prevState => ({ count: prevState.count + 1 }));
      console.log('Async Update Complete (via setTimeout):', this.state.count);
      console.log('--- Async Click batch complete ---');
    }, 0);
  };

  render() {
    return (
      <div>
        <h1>Async setState Demo</h1>
        <p>Count: {this.state.count}</p>
        <button onClick={this.handleSyncClick}>Increment Sync (x2)</button>
        <button onClick={this.incrementTwiceAsync}>Increment Async (x2)</button>
      </div>
    );
  }
}

export default AsyncCounter;

