import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';

// Create a dedicated DOM node for modals outside the root app element in public/index.html
// <div id="modal-root"></div>

const Modal = ({ children, isOpen, onClose }) => {
  const modalRootRef = useRef(null);

  // Ensure the modal root element exists and cache it
  useEffect(() => {
    modalRootRef.current = document.getElementById('modal-root') || document.createElement('div');
    if (!document.getElementById('modal-root')) {
      modalRootRef.current.id = 'modal-root';
      document.body.appendChild(modalRootRef.current);
    }
  }, []);

  if (!isOpen || !modalRootRef.current) return null;

  return ReactDOM.createPortal(
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000 // High z-index to overlay everything
    }} onClick={onClose}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        minWidth: '300px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
        zIndex: 1001 // Higher z-index for modal content
      }} onClick={e => e.stopPropagation()}>
        {children}
        <button onClick={onClose} style={{ marginTop: '20px' }}>Close Modal</button>
      </div>
    </div>,
    modalRootRef.current // The DOM node where the modal content will be rendered
  );
