// Imagine a simplified WebSocket client
const MockWebSocket = {
  _listeners: [],
  connect: () => console.log('WebSocket connected'),
  disconnect: () => console.log('WebSocket disconnected'),
  subscribe: (callback) => {
    MockWebSocket._listeners.push(callback);
    console.log('Subscribed to WebSocket');
    // Simulate incoming messages
    MockWebSocket.interval = setInterval(() => {
      const message = `Message from server: ${Date.now()}`;
      MockWebSocket._listeners.forEach(cb => cb(message));
    }, 2000);
  },
  unsubscribe: (callback) => {
    MockWebSocket._listeners = MockWebSocket._listeners.filter(cb => cb !== callback);
    console.log('Unsubscribed from WebSocket');
    if (MockWebSocket._listeners.length === 0) {
      clearInterval(MockWebSocket.interval);
      console.log('No more subscribers, stopping message simulation.');
    }
  }
};

class ChatClient extends React.Component {
  constructor(props) {
    super(props);
    this.state = { messages: [] };
    this.handleNewMessage = this.handleNewMessage.bind(this);
  }

  handleNewMessage(message) {
    this.setState(prevState => ({
      messages: [...prevState.messages, message]
    }));
  }

  componentDidMount() {
    console.log('ChatClient Mounted');
    // Subscribe to the WebSocket feed
    MockWebSocket.connect();
    MockWebSocket.subscribe(this.handleNewMessage);
  }

  componentWillUnmount() {
    console.log('ChatClient Will Unmount');
    // Unsubscribe from the WebSocket feed to prevent memory leaks
    MockWebSocket.unsubscribe(this.handleNewMessage);
    MockWebSocket.disconnect();
  }

  render() {
    return (
      <div>
        <h3>Chat Messages</h3>
        <div style={{ height: '150px', overflowY: 'scroll', border: '1px solid #ccc', padding: '10px' }}>
          {this.state.messages.length === 0 ? (
            <p>Waiting for messages...</p>
          ) : (
            <ul>
              {this.state.messages.map((msg, index) => (
                <li key={index}>{msg}</li>
              ))}
            </ul>
          )}
        </div>
        <p>This component will log subscribe/unsubscribe actions to console.</p>
      </div>
    );
  }
}

