import React, { useState, useRef, useEffect } from 'react';
import ReactDOM from 'react-dom';

// Assume this div exists in public/index.html:
// <div id="tooltip-root"></div>

const Tooltip = ({ content, position, isVisible }) => {
  if (!isVisible || !position) return null;

  const tooltipRoot = document.getElementById('tooltip-root');
  if (!tooltipRoot) {
    console.error('Tooltip root element #tooltip-root not found!');
    return null;
  }

  const tooltipStyle = {
    position: 'absolute',
    top: position.top + position.height + 5, // 5px offset below trigger
    left: position.left,
    backgroundColor: 'black',
    color: 'white',
    padding: '8px',
    borderRadius: '4px',
    zIndex: 9999, // Ensure it's on top
    whiteSpace: 'nowrap'
  };

  return ReactDOM.createPortal(
    <div style={tooltipStyle}>
      {content}
    </div>,
    tooltipRoot
  );
};

function TriggerWithTooltip({ children, tooltipContent }) {
  const [showTooltip, setShowTooltip] = useState(false);
  const [triggerRect, setTriggerRect] = useState(null);
  const triggerRef = useRef(null);

  const handleMouseEnter = () => {
    if (triggerRef.current) {
      // Get the bounding rectangle of the trigger element
      setTriggerRect(triggerRef.current.getBoundingClientRect());
    }
    setShowTooltip(true);
  };

  const handleMouseLeave = () => {
    setShowTooltip(false);
  };

  return (
    <span
      ref={triggerRef}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{ display: 'inline-block', cursor: 'pointer' }}
    >
      {children}
      <Tooltip
        content={tooltipContent}
        position={triggerRect}
        isVisible={showTooltip}
      />
    </span>
  );
}

// Example Usage in an App component
function AppPortalTooltip() {
  return (
    <div style={{ padding: '50px', height: '200vh' }}>
      <h1>React Portal Tooltip Example</h1>
      <div style={{ position: 'relative', border: '1px solid blue', padding: '20px', overflow: 'hidden', height: '100px' }}>
        <p>This is some content inside a container with `overflow: hidden`.</p>
        <TriggerWithTooltip tooltipContent="This is a tooltip for the first trigger!">
          <button>Hover me (1)</button>
        </TriggerWithTooltip>
        <p style={{ marginTop: '20px' }}>More content.</p>
      </div>
      <p style={{ marginTop: '50px' }}>
        Here is another 
        <TriggerWithTooltip tooltipContent="And another tooltip here!">
          <span>text element</span>
        </TriggerWithTooltip> 
        that also triggers a tooltip.
      </p>
    </div>
  );
}
