import React, { useState } from 'react';

let nextProductId = 1;

function ProductItem({ product, onRemove }) {
  const [quantity, setQuantity] = useState(0);

  // Using product.id as the key is crucial here for state persistence.
  // If `key={index}` were used instead, adding to the beginning or
  // removing from the middle would shift indices, causing React to
  // mistakenly re-use the wrong component instance and its 'quantity' state
  // for a different product, leading to incorrect input values.
  // With a stable 'id' as key, React knows exactly which component instance
  // corresponds to which product, preserving its `quantity` state.

  return (
    <li style={{ border: '1px solid #eee', padding: '10px', margin: '5px 0', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <span>Product: {product.name} (ID: {product.id})</span>
      <div>
        <label>Qty: </label>
        <input 
          type="number" 
          value={quantity} 
          onChange={(e) => setQuantity(parseInt(e.target.value) || 0)} 
          style={{ width: '60px' }}
        />
        <button onClick={() => onRemove(product.id)} style={{ marginLeft: '10px' }}>Remove</button>
      </div>
    </li>
  );
}

function ProductListManager() {
  const [products, setProducts] = useState([
    { id: nextProductId++, name: 'Product A' },
    { id: nextProductId++, name: 'Product B' },
    { id: nextProductId++, name: 'Product C' }
  ]);

  const addProductToBeginning = () => {
    const newProduct = { id: nextProductId++, name: `New Product ${nextProductId - 1}` };
    setProducts([newProduct, ...products]);
  };

  const removeProductById = (idToRemove) => {
    setProducts(products.filter(product => product.id !== idToRemove));
  };

  return (
    <div>
      <h2>Dynamic Product List (Stable Keys)</h2>
      <button onClick={addProductToBeginning}>Add New Product to Beginning</button>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {products.map(product => (
          <ProductItem 
            key={product.id} // CRITICAL: Using stable product.id as key
            product={product} 
            onRemove={removeProductById} 
          />
        ))}
      </ul>
      <p>Try entering quantities, then add/remove items. Observe how quantities remain tied to the correct product.</p>
      <p>Now, try changing `key={product.id}` to `key={index}` in `ProductListManager` and repeat. You'll see quantities shift to the wrong products.</p>
    </div>
  );
}

// Example Usage:
// <ProductListManager />
