  const [error, setError] = useState(null);

  useEffect(() => {
    // Reset states for new fetch
    setLoading(true);
    setError(null);
    setUserData(null); 

    const fetchUser = async () => {
      try {
        const response = await fetch(`https://jsonplaceholder.typicode.com/users/${userId}`);
        if (!response.ok) {
          // Handle 404 or other HTTP errors
          if (response.status === 404) {
              throw new Error(`User with ID ${userId} not found.`);
          } else {
              throw new Error(`HTTP error! status: ${response.status}`);
          }
        }
        const data = await response.json();
        setUserData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();

    // Cleanup function (optional for fetch, but good practice for subscriptions/timers)
    // For fetch, typically not needed unless you use AbortController
    return () => {
      // Any cleanup if necessary, e.g., cancelling ongoing requests
    };
  }, [userId]); // Dependency array: re-run effect if userId changes

  if (loading) {
    return <div>Loading user {userId} data...</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>Error: {error.message}</div>;
  }

  if (!userData) {
    // This case should ideally not be reached if loading/error are handled correctly
    // but serves as a fallback for initial state or unexpected null data.
    return <div>No user data available for user ID {userId}.</div>;
  }

  return (
    <div style={{ border: '1px solid #ccc', padding: '15px', margin: '10px', borderRadius: '8px' }}>
      <h2>User Profile: {userData.name}</h2>
      <p>Email: {userData.email}</p>
      <p>Phone: {userData.phone}</p>
      <p>Website: {userData.website}</p>
    </div>
  );
}

