import React from 'react';

class DebugMe extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }

  incrementCount = () => { // Renamed for clarity, using class property for binding
    this.setState(prevState => ({ count: prevState.count + 1 }));
  };

  render() {
    // PROBLEM EXPLANATION: 
    // Calling `this.triggerUpdate()` (or `this.incrementCount()`) directly in `render` 
    // causes an infinite loop because `setState()` inside it triggers a re-render,
    // which in turn calls `render()` again, leading to a continuous cycle.
    // The `render` method must be a pure function that only returns JSX 
    // based on current props and state, without causing side effects like state changes.

    return (
      <div>
        <h1>Count: {this.state.count}</h1>
        <p>This component is now fixed!</p>
        {/* SOLUTION: Call the method only on a user interaction, e.g., button click */}
        <button onClick={this.incrementCount}>Increment Count</button>
      </div>
    );
  }
}

export default DebugMe;

