import React, { useState, useRef } from 'react';

function ContactForm() {
  // Controlled inputs: state manages their value
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');

  // Uncontrolled inputs: refs access their DOM value
  const subscribeRef = useRef(null);
  const resumeFileRef = useRef(null);

  const handleSubmit = (event) => {
    event.preventDefault();

    // Accessing controlled input values from state
    const enteredName = name;
    const enteredMessage = message;

    // Accessing uncontrolled input values from refs
    const isSubscribed = subscribeRef.current.checked; // For checkbox, use .checked
    const resumeFileName = resumeFileRef.current.files.length > 0 
      ? resumeFileRef.current.files[0].name 
      : 'No file selected';

    const formData = {
      name: enteredName,
      message: enteredMessage,
      subscribe: isSubscribed,
      resumeFileName: resumeFileName
    };
    console.log('Form Submitted:', formData);
    alert('Form submitted! Check console for details.');

    // Optional: Clear controlled fields after submission
    setName('');
    setMessage('');
    // Note: Cannot easily clear uncontrolled fields programmatically without direct DOM manipulation 
    // or making them controlled.
    if (resumeFileRef.current) resumeFileRef.current.value = null; // Clears file input
    if (subscribeRef.current) subscribeRef.current.checked = false; // Unchecks checkbox
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px', maxWidth: '400px', margin: '20px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>Contact Us</h2>

      <div>
        <label htmlFor="name">Name (Controlled):</label>
        <input
          id="name"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <small>Characters: {name.length}</small>
      </div>

      <div>
        <label htmlFor="message">Message (Controlled):</label>
        <textarea
          id="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          rows="5"
        ></textarea>
      </div>

      <div>
        <input
          id="subscribe"
          type="checkbox"
          ref={subscribeRef}
        />
        <label htmlFor="subscribe">Subscribe to Newsletter (Uncontrolled)</label>
      </div>

      <div>
        <label htmlFor="resume">Attach Resume (Uncontrolled):</label>
        <input
          id="resume"
          type="file"
          ref={resumeFileRef}
        />
      </div>

      <button type="submit" style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
        Submit Form
      </button>
    </form>
  );
}

export default ContactForm;

