const blogPosts = [
  { id: '1', title: 'First Steps with React', content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.' },
  { id: '2', title: 'Understanding Hooks', content: 'Consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.' },
  { id: '3', title: 'Styling in React', content: 'Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.' }
];

// 1. Home Page Component
const HomePage = () => (
  <div>
    <h2>Welcome to Our Blog!</h2>
    <p>Explore our articles and learn something new.</p>
    <nav>
      <ul>
        <li><Link to="/about">About Us</Link></li>
        <li><Link to="/posts">View All Posts</Link></li>
      </ul>
    </nav>
  </div>
);

// 2. About Page Component
const AboutPage = () => (
  <div>
    <h2>About This Blog</h2>
    <p>This blog is dedicated to sharing knowledge about frontend development.</p>
  </div>
);

// 3. Blog Posts List Page Component
const PostsListPage = () => (
  <div>
    <h2>All Blog Posts</h2>
    <ul>
      {blogPosts.map(post => (
        <li key={post.id}>
          <Link to={`/posts/${post.id}`}>{post.title}</Link>
        </li>
      ))}
    </ul>
  </div>
);

// 4. Blog Post Detail Page Component
const PostDetailPage = ({ match }) => {
  const postId = match.params.postId; // Get postId from match.params
  const post = blogPosts.find(p => p.id === postId); // Find the post

  if (!post) {
    return (
      <div>
        <h2>Post Not Found</h2>
        <p>The blog post with ID "{postId}" does not exist.</p>
        <Link to="/posts">Back to all posts</Link>
      </div>
    );
  }

  return (
    <div>
      <h2>{post.title}</h2>
      <p>{post.content}</p>
      <Link to="/posts">Back to all posts</Link>
    </div>
  );
};

// 5. Not Found Page Component
const NotFoundPage = () => (
  <div>
    <h1>404 - Page Not Found</h1>
    <p>The page you are looking for does not exist.</p>
    <Link to="/">Go to Home</Link>
  </div>
);

function App() {
  return (
    <BrowserRouter>
      <nav style={{ padding: '10px', backgroundColor: '#f0f0f0', borderBottom: '1px solid #ddd' }}>
        <Link to="/" style={{ margin: '0 10px' }}>Home</Link>
        <Link to="/about" style={{ margin: '0 10px' }}>About</Link>
        <Link to="/posts" style={{ margin: '0 10px' }}>Blog</Link>
      </nav>
      <div style={{ padding: '20px' }}>
        <Switch>
          {/* Define all routes here */}
          <Route exact path="/" component={HomePage} />
          <Route path="/about" component={AboutPage} />
          <Route exact path="/posts" component={PostsListPage} />
          <Route path="/posts/:postId" component={PostDetailPage} />
          {/* Catch-all for 404 - must be last */}
          <Route component={NotFoundPage} />
        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;

