import React, { useState } from 'react';

let nextItemId = 1;

function ChecklistItem({ item, onToggleComplete, onRemove }) {
  return (
    <li style={{ display: 'flex', alignItems: 'center', margin: '5px 0', border: '1px solid #eee', padding: '8px', borderRadius: '4px' }}>
      <input
        type="checkbox"
        checked={item.completed}
        onChange={() => onToggleComplete(item.id)}
        style={{ marginRight: '10px' }}
      />
      <span style={{ textDecoration: item.completed ? 'line-through' : 'none' }}>
        {item.text} (ID: {item.id})
      </span>
      <button
        onClick={() => onRemove(item.id)}
        style={{ marginLeft: 'auto', backgroundColor: '#dc3545', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '3px', cursor: 'pointer' }}
      >
        Remove
      </button>
    </li>
  );
}

function Checklist() {
  const [items, setItems] = useState([
    { id: nextItemId++, text: 'Learn React Keys', completed: false },
    { id: nextItemId++, text: 'Understand PropTypes', completed: true },
    { id: nextItemId++, text: 'Implement a Modal', completed: false }
  ]);

  const handleAddItem = () => {
    const newItem = { id: nextItemId++, text: `New Task ${nextItemId - 1}`, completed: false };
    setItems([newItem, ...items]); // Solution: Add new item to the beginning
  };

  const handleToggleComplete = (id) => {
    setItems(items.map(item => 
      item.id === id ? { ...item, completed: !item.completed } : item
    ));
  };

  const handleRemoveItem = (idToRemove) => {
    setItems(items.filter(item => item.id !== idToRemove)); // Solution: Remove item by id
  };

  return (
    <div style={{ padding: '20px', maxWidth: '500px', margin: 'auto' }}>
      <h1>My Dynamic Checklist</h1>
      <button 
        onClick={handleAddItem} 
        style={{ marginBottom: '15px', padding: '10px 15px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
      >
        Add New Task to Top
      </button>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {items.map(item => (
          <ChecklistItem 
            key={item.id} // Solution: Using stable item.id as key
            item={item} 
            onToggleComplete={handleToggleComplete} 
            onRemove={handleRemoveItem} 
          />
        ))}
      </ul>
      <p style={{ marginTop: '20px', fontSize: '0.9em', color: '#555' }}>
        **Explanation of Key Usage:**
        Using `item.id` as the `key` is crucial here because `id` is a stable, unique identifier for each task.
        If a new item is added to the beginning, or an item is removed from the middle, the `id` of existing items remains unchanged.
        React uses these stable `id`s to correctly identify and match existing `ChecklistItem` components in the new list with their previous instances.
        This ensures that the internal state of each `ChecklistItem` (specifically, its checkbox `checked` state, which is implicitly managed by the DOM input element linked to `item.completed`) is preserved and correctly associated with the original task, even if its position in the array (and thus its `index`) changes.

        In contrast, if `key={index}` were used, when an item is added to the beginning or removed from the middle, the indices of all subsequent items would shift. React would then see that the item at `index 0` is now a different item, and try to re-use the *previous* `index 0`'s DOM element and its state for the *new* item at `index 0`, leading to incorrect checkbox states or other unexpected behavior.
      </p>
    </div>
  );
}

export default Checklist;

