import { createStore } from 'redux';

// 1. Define Action Types
const INCREMENT = 'INCREMENT';
const DECREMENT = 'DECREMENT';
const RESET = 'RESET';

// 2. Action Creators
function increment() {
  return { type: INCREMENT };
}

function decrement() {
  return { type: DECREMENT };
}

function reset() {
  return { type: RESET };
}

// 3. Reducer
const initialState = { count: 0 };

function counterReducer(state = initialState, action) {
  switch (action.type) {
    case INCREMENT:
      return { ...state, count: state.count + 1 };
    case DECREMENT:
      return { ...state, count: state.count - 1 };
    case RESET:
      return { ...state, count: 0 };
    default:
      return state;
  }
}

// 4. Create Store
const store = createStore(counterReducer);

// 5. Dispatch Actions and Log State
console.log('Initial state:', store.getState()); // { count: 0 }

store.dispatch(increment());
console.log('After increment:', store.getState()); // { count: 1 }

store.dispatch(increment());
console.log('After second increment:', store.getState()); // { count: 2 }

store.dispatch(decrement());
console.log('After decrement:', store.getState()); // { count: 1 }

store.dispatch(reset());
console.log('After reset:', store.getState()); // { count: 0 }

store.dispatch({ type: 'UNKNOWN_ACTION' });
console.log('After unknown action:', store.getState()); // { count: 0 } (state remains unchanged)

