      setTimeout(() => {
        if (userId === 1) {
          resolve({ id: 1, name: 'John Doe', email: 'john@example.com' });
        } else if (userId === 2) {
          reject(new Error('User not found'));
        } else {
          reject(new Error('Invalid user ID'));
        }
      }, 1000); // Simulate network delay
    });
