import React, { Component } from 'react';

interface UserData {
  id: number;
  name: string;
  email: string;
  username: string;
  address: any;
  phone: string;
  website: string;
  company: any;
}

interface UserProfileFetcherState {
  loading: boolean;
  error: Error | null;
  userData: UserData | null;
}

class UserProfileFetcher extends Component<{}, UserProfileFetcherState> {
  constructor(props: {}) {
    super(props);
    this.state = {
      loading: true,
      error: null,
      userData: null,
    };
  }

  componentDidMount() {
    this.fetchUserProfile();
  }

  fetchUserProfile = () => {
    this.setState({ loading: true, error: null, userData: null }); // Reset state on retry
    fetch('https://jsonplaceholder.typicode.com/users/1')
      .then(response => {
        if (!response.ok) {
          // Throw an error for HTTP status codes that indicate failure
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data: UserData) => {
        this.setState({ userData: data, loading: false });
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        this.setState({ error: new Error(`Failed to fetch user data: ${error.message}`), loading: false });
      });
  };

  render() {
    const { loading, error, userData } = this.state;

    return (
      <div style={{ padding: '20px', border: '1px solid #eee', borderRadius: '8px', maxWidth: '400px', margin: '20px auto' }}>
        <h2>User Profile</h2>
        {loading && <p>Loading user data...</p>}

        {error && (
          <div style={{ color: 'red' }}>
            <p>{error.message}</p>
            <button onClick={this.fetchUserProfile}>Retry</button>
          </div>
        )}

        {userData && !loading && !error && (
          <div>
            <h3>{userData.name}</h3>
            <p><strong>Email:</strong> {userData.email}</p>
            <p><strong>Username:</strong> {userData.username}</p>
            <p><strong>Website:</strong> {userData.website}</p>
          </div>
        )}

        {!loading && !error && !userData && <p>No user data available.</p>}
      </div>
    );
  }
}

export default UserProfileFetcher;

