import React from 'react';

class ChildComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      localCount: 0
    };
  }

  shouldComponentUpdate(nextProps, nextState) {
    console.log('ChildComponent: shouldComponentUpdate called');
    // Only re-render if parentCount or localCount changes
    return nextProps.parentCount !== this.props.parentCount || nextState.localCount !== this.state.localCount;
  }

  handleStateUpdate = () => {
    console.log('ChildComponent: setState called');
    this.setState(prevState => ({ localCount: prevState.localCount + 1 }));
  };

  handleForceUpdate = () => {
    console.log('ChildComponent: forceUpdate called');
    this.forceUpdate();
  };

  render() {
    console.log('ChildComponent: Render method called');
    return (
      <div style={{ border: '1px solid black', padding: '10px', margin: '10px' }}>
        <h3>Child Component (Prop: {this.props.parentCount})</h3>
        <p>Local State Count: {this.state.localCount}</p>
        <p>Current Timestamp: {new Date().toLocaleTimeString()}</p>
        <button onClick={this.handleStateUpdate}>Update Local State (setState)</button>
        <button onClick={this.handleForceUpdate}>Force Re-render (forceUpdate)</button>
      </div>
    );
  }
}

class ParentComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      parentCounter: 0
    };
