  handleParentPropChange = () => {
    console.log('ParentComponent: setState called to update child prop');
    this.setState(prevState => ({ parentCounter: prevState.parentCounter + 1 }));
  };

  render() {
    console.log('ParentComponent: Render method called');
    return (
