import React from 'react';

class CountdownTimer extends React.Component {
  constructor(props) {
    super(props);
    this.state = { secondsLeft: 10 };
    this.timerId = null; // To store the interval ID
  }

  componentDidMount() {
    console.log('CountdownTimer: Component mounted, starting timer.');
    this.timerId = setInterval(() => {
      this.setState(prevState => {
        if (prevState.secondsLeft > 0) {
          return { secondsLeft: prevState.secondsLeft - 1 };
        } else {
          clearInterval(this.timerId);
          return null; // Stop updating state if already 0
        }
      });
    }, 1000);
  }

  componentWillUnmount() {
    if (this.timerId) {
      clearInterval(this.timerId);
      console.log('CountdownTimer: Component unmounted, timer cleared.');
