import React from 'react';

class ResizableTextArea extends React.Component {
  constructor(props) {
    super(props);
    this.state = { textareaWidth: 0 };
    this.textareaRef = React.createRef(); // Create a ref for the textarea
  }

  handleTextAreaResize = () => {
    if (this.textareaRef.current) {
      const newWidth = this.textareaRef.current.offsetWidth;
      // Only update if width actually changed to avoid unnecessary re-renders
      if (newWidth !== this.state.textareaWidth) {
        this.setState({ textareaWidth: newWidth });
      }
    }
  };

  componentDidMount() {
    console.log('ResizableTextArea: Mounted, attaching listener.');
    // Attach the event listener to the textarea element itself
    if (this.textareaRef.current) {
      this.textareaRef.current.addEventListener('resize', this.handleTextAreaResize);
      // Set initial width
      this.setState({ textareaWidth: this.textareaRef.current.offsetWidth });
    }
  }

  componentWillUnmount() {
    console.log('ResizableTextArea: Unmounting, removing listener.');
    // Remove the event listener
    if (this.textareaRef.current) {
      this.textareaRef.current.removeEventListener('resize', this.handleTextAreaResize);
    }
  }

  // Challenge: If the ref itself could change (e.g., conditional rendering affecting the element)
  // componentDidUpdate(prevProps, prevState) {
  //   if (this.textareaRef.current && this.textareaRef.current !== this.prevTextareaRef.current) {
  //     // If ref changed, remove listener from old and add to new
  //     if (this.prevTextareaRef.current) {
  //       this.prevTextareaRef.current.removeEventListener('resize', this.handleTextAreaResize);
  //     }
  //     this.textareaRef.current.addEventListener('resize', this.handleTextAreaResize);
  //   }
  //   this.prevTextareaRef.current = this.textareaRef.current; // Store current ref for next update
  // }

  render() {
    return (
      <div style={{ padding: '20px' }}>
        <h3>Resizable Textarea</h3>
        <p>Current Textarea Width: {this.state.textareaWidth}px</p>
        <textarea
          ref={this.textareaRef}
          style={{
            width: '300px',
            height: '100px',
            resize: 'both',
            overflow: 'auto',
            border: '1px solid grey',
            boxSizing: 'border-box' // Important for predictable sizing
          }}
          placeholder="Try resizing me!"
        ></textarea>
      </div>
    );
  }
}

// Parent component for testing unmount
