import React, { useState } from 'react';

function TodoList() {
  const [todos, setTodos] = useState([
    { id: 1, task: 'Learn React Hooks' },
    { id: 2, task: 'Master JavaScript' },
    { id: 3, task: 'Build a Portfolio' },
  ]);

  const [newTodoText, setNewTodoText] = useState('');

  const addTodo = () => {
    if (newTodoText.trim() === '') return; // Prevent adding empty todos
    const newTodo = { id: Date.now(), task: newTodoText.trim() }; // Use Date.now() for unique ID
    setTodos([...todos, newTodo]);
    setNewTodoText(''); // Clear input after adding
  };

  const removeTodo = (idToRemove) => {
    setTodos(todos.filter(todo => todo.id !== idToRemove));
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: '0 auto', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>My Todo List</h2>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Add a new todo"
          value={newTodoText}
          onChange={(e) => setNewTodoText(e.target.value)}
          style={{ flexGrow: 1, padding: '8px' }}
        />
        <button onClick={addTodo} style={{ padding: '8px 15px' }}>Add Todo</button>
      </div>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {todos.map((todo) => (
          // Correct Key Usage: using item.id ensures stable identity
          <li key={todo.id} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px 0', borderBottom: '1px dashed #eee' }}>
            <span>{todo.task}</span>
            <button onClick={() => removeTodo(todo.id)} style={{ background: '#ff4d4d', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '4px', cursor: 'pointer' }}>Remove</button>
          </li>
        ))}
