import PropTypes from 'prop-types';

function UserProfile({ name, age, isActive, hobbies, onUpdate, contactInfo, level }) {
  return (
    <div>
      <h2>{name}</h2>
      <p>Age: {age}</p>
      <p>Status: {isActive ? 'Active' : 'Inactive'}</p>
      <ul>
        {hobbies.map(hobby => <li key={hobby}>{hobby}</li>)}
      </ul>
      {contactInfo && <p>Email: {contactInfo.email}</p>}
      <p>Level: {level}</p>
      <button onClick={onUpdate}>Update Profile</button>
    </div>
  );
}

UserProfile.propTypes = {
  name: PropTypes.string.isRequired,
  age: PropTypes.number,
  isActive: PropTypes.bool.isRequired,
  hobbies: PropTypes.arrayOf(PropTypes.string),
  onUpdate: PropTypes.func.isRequired,
  // Complex object shape validation
  contactInfo: PropTypes.shape({
    email: PropTypes.string.isRequired,
    phone: PropTypes.string
  }),
  // Custom validator: ensures level is between 1-10
  level: function(props, propName, componentName) {
    const value = props[propName];
    if (value === undefined) {
      // Allow undefined if not required, or handle via defaultProps
      return null; 
    }
    if (typeof value !== 'number' || value < 1 || value > 10) {
      return new Error(
        `Invalid prop \`${propName}\` supplied to \`${componentName}\`. Level must be a number between 1-10.`
      );
    }
    return null;
  }
};

// Default prop values are applied before propTypes validation
UserProfile.defaultProps = {
  age: 25,
  hobbies: [],
  contactInfo: { email: 'user@example.com' },
  level: 5 // Example default level
};

// Example Usage:
