import React, { Component } from 'react';

class TextReverser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: ''
    };
  }

  // Implemented as a class property arrow function to auto-bind 'this'
  onChangeHandler = (e) => {
    this.setState({
      inputValue: e.target.value
    });
  };

  // Helper function to reverse the string (optional, can be inline in render)
  reverseString = (str) => {
    return str.split('').reverse().join('');
  };

  render() {
    const { inputValue } = this.state;
    const reversedValue = this.reverseString(inputValue); // Using the helper

    return (
      <div>
        <h2>Text Reverser</h2>
        <input
          type="text"
          value={inputValue}
          onChange={this.onChangeHandler}
          placeholder="Type something..."
        />
        <p>Original Text: {inputValue}</p>
        <p>Reversed Text: {reversedValue}</p>
