// @flow
import * as React from 'react';

type Comment = {
  id: number,
  text: string,
};

type PostProps = {
  title: string,
  content: string,
  author: string,
  likes?: number,
  comments?: Array<Comment>,
  onLike: () => void,
};

function PostDisplay(props: PostProps) {
  const { title, content, author, likes, comments, onLike } = props;

  // Using optional chaining and nullish coalescing for graceful handling of optional props
  const actualLikes = likes ?? 0; // If likes is undefined or null, use 0
  const actualComments = comments ?? []; // If comments is undefined or null, use empty array

  return (
    <div style={{ border: '1px solid #ccc', padding: '20px', margin: '15px', borderRadius: '8px' }}>
      <h2>{title}</h2>
      <p><em>By {author}</em></p>
      <p>{content}</p>
      <p>Likes: {actualLikes}</p>
      <button onClick={onLike}>Like Post</button>
      <h4>Comments:</h4>
      {actualComments.length > 0 ? (
        <ul>
          {actualComments.map(comment => (
            <li key={comment.id}>{comment.text}</li>
          ))}
        </ul>
      ) : (
        <p>No comments yet.</p>
      )}
    </div>
  );
}

// It's still common practice to define defaultProps for runtime behavior,
// even with static type checkers. The type definition ensures consistency.
PostDisplay.defaultProps = {
  likes: 0,
  comments: [],
};

export default PostDisplay;
