import React from 'react';
import { Routes, Route, Outlet, Link, BrowserRouter } from 'react-router-dom';

// 1. Dashboard Layout Component
const DashboardLayout = () => (
  <div style={{ display: 'flex', height: '100vh' }}>
    <nav style={{ padding: '20px', borderRight: '1px solid #ccc', minWidth: '150px' }}>
      <h3>Dashboard Nav</h3>
      <ul>
        <li><Link to="/dashboard">Overview</Link></li>
        <li><Link to="reports">Reports</Link></li>
        <li><Link to="settings">Settings</Link></li>
      </ul>
    </nav>
    <main style={{ flexGrow: 1, padding: '20px' }}>
      {/* 2. The Outlet component renders the matched child route */}
      <Outlet /> 
    </main>
  </div>
);

// 3. Child Components
const DashboardOverview = () => (
  <div>
    <h2>Dashboard Overview</h2>
    <p>Welcome to your dashboard overview.</p>
  </div>
);

const DashboardReports = () => (
  <div>
    <h2>Reports</h2>
    <p>View your performance reports here.</p>
  </div>
);

const DashboardSettings = () => (
  <div>
    <h2>Settings</h2>
    <p>Manage your account settings.</p>
  </div>
);

// Main App Component for Routing
const App = () => (
  <BrowserRouter>
    <Routes>
      {/* Route for the Dashboard Layout and its children */}
      <Route path="/dashboard" element={<DashboardLayout />}>
        {/* Index route for /dashboard */}
        <Route index element={<DashboardOverview />} />
        {/* Nested routes for /dashboard/reports and /dashboard/settings */}
        <Route path="reports" element={<DashboardReports />} />
        <Route path="settings" element={<DashboardSettings />} />
      </Route>
      {/* A simple home page */}
      <Route path="/" element={<h1>Welcome to the App!</h1>} />
      {/* Catch-all for unmatched routes */}
      <Route path="*" element={<h2>404 Not Found</h2>} />
    </Routes>
  </BrowserRouter>
);

export default App;
