import React, { useState, useEffect, createContext, useContext } from 'react';
import { BrowserRouter, Routes, Route, useNavigate, Link, Navigate } from 'react-router-dom';

// Create an AuthContext to manage authentication state globally
const AuthContext = createContext(null);

// Auth Provider component to wrap the application
const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const login = () => setIsLoggedIn(true);
  const logout = () => setIsLoggedIn(false);

  const authContextValue = { isLoggedIn, login, logout };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

// HomePage component
const HomePage = () => {
  const { isLoggedIn } = useContext(AuthContext);
  return (
    <div>
      <h2>Home Page</h2>
      <p>Welcome! You are {isLoggedIn ? 'logged in.' : 'not logged in.'}</p>
    </div>
  );
};

// LoginPage component
const LoginPage = () => {
  const navigate = useNavigate();
  const { isLoggedIn, login } = useContext(AuthContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    if (isLoggedIn) {
      navigate('/dashboard', { replace: true }); // Use replace to prevent back button to login
    }
  }, [isLoggedIn, navigate]);

  const handleLogin = () => {
    // Implement actual login logic here (e.g., API call)
    // For this task, we simulate success for 'user'/'pass'
    if (username === 'user' && password === 'pass') {
      login(); // Call login from context
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div>
      <h2>Login Page</h2>
      <input
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        placeholder="Username"
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

// DashboardPage component
const DashboardPage = () => {
  const { logout } = useContext(AuthContext);
  return (
    <div>
      <h2>Dashboard Page</h2>
      <p>This is a protected dashboard.</p>
      <button onClick={logout}>Logout</button>
    </div>
  );
};

// ProtectedRoute Wrapper
const ProtectedRoute = ({ children }) => {
  const { isLoggedIn } = useContext(AuthContext);
  if (!isLoggedIn) {
    // Redirect to login if not authenticated
    return <Navigate to="/login" replace />;
  }
  return children;
};

// Main App component to set up routes
const App = () => {
  return (
    <BrowserRouter>
      <AuthProvider>
        <nav style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>
          <Link to="/" style={{ margin: '0 10px' }}>Home</Link>
          <Link to="/login" style={{ margin: '0 10px' }}>Login</Link>
          <Link to="/dashboard" style={{ margin: '0 10px' }}>Dashboard</Link>
        </nav>
        <hr/>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          {/* Dashboard route is protected using the ProtectedRoute component */}
          <Route path="/dashboard" element={
            <ProtectedRoute>
              <DashboardPage />
            </ProtectedRoute>
          } />
          <Route path="*" element={<h2>404 Not Found</h2>} />
        </Routes>
      </AuthProvider>
    </BrowserRouter>
  );
};

export default App;

