import { BrowserRouter, Routes, Route, Outlet, Link } from 'react-router-dom';

// DashboardLayout component
const DashboardLayout = () => {
  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <nav style={{ padding: '20px', borderRight: '1px solid #ccc', minWidth: '150px' }}>
        <h3>Dashboard Navigation</h3>
        <ul>
          <li><Link to="/dashboard">Overview</Link></li>
          <li><Link to="profile">Profile</Link></li>
          <li><Link to="settings">Settings</Link></li>
        </ul>
      </nav>
      <main style={{ flexGrow: 1, padding: '20px' }}>
        {/* The Outlet component renders the matched child route */}
        <Outlet /> 
      </main>
    </div>
  );
};

// DashboardOverview component
const DashboardOverview = () => (
  <div>
    <h2>Dashboard Overview</h2>
    <p>Welcome to your personalized dashboard overview.</p>
  </div>
);

// UserProfile component
const UserProfile = () => (
  <div>
    <h2>User Profile</h2>
    <p>Manage your profile information here.</p>
  </div>
);

// AppSettings component
const AppSettings = () => (
  <div>
    <h2>Application Settings</h2>
    <p>Configure application settings.</p>
  </div>
);

const App = () => {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<h1>Home Page</h1>} />
        {/* Parent route for dashboard layout and nested routes */}
        <Route path="dashboard" element={<DashboardLayout />}>
          {/* Index route for /dashboard (renders when parent path exactly matches) */}
          <Route index element={<DashboardOverview />} />
          {/* Nested routes. Paths are relative to the parent route's path. */}
          <Route path="profile" element={<UserProfile />} />
          <Route path="settings" element={<AppSettings />} />
        </Route>
        {/* Catch-all route for any unmatched paths */}
        <Route path="*" element={<h2>404 Not Found</h2>} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;

