import React from 'react';
import { BrowserRouter, Routes, Route, Outlet, Link, useParams } from 'react-router-dom';

// Reusing components from task_implement_dashboard_layout:
const DashboardLayout = () => {
  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <nav style={{ padding: '20px', borderRight: '1px solid #ccc', minWidth: '150px' }}>
        <h3>Dashboard Navigation</h3>
        <ul>
          <li><Link to="/dashboard">Overview</Link></li>
          <li><Link to="profile">Profile</Link></li>
          <li><Link to="settings">Settings</Link></li>
          <li><Link to="users">Users</Link></li> {/* New link for Users */}
        </ul>
      </nav>
      <main style={{ flexGrow: 1, padding: '20px' }}>
        <Outlet /> 
      </main>
    </div>
  );
};

const DashboardOverview = () => (
  <div>
    <h2>Dashboard Overview</h2>
    <p>Welcome to your personalized dashboard overview.</p>
  </div>
);

const UserProfile = () => (
  <div>
    <h2>User Profile</h2>
    <p>Manage your profile information here.</p>
  </div>
);

const AppSettings = () => (
  <div>
    <h2>Application Settings</h2>
    <p>Configure application settings.</p>
  </div>
);

// New: UserListPage component
const UserListPage = () => {
  const users = ['alice', 'bob', 'charlie']; // Using lowercase for URL consistency
  return (
    <div>
      <h2>User List</h2>
      <ul>
        {users.map(user => (
          <li key={user}>
            <Link to={user}>{user.charAt(0).toUpperCase() + user.slice(1)}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

// New: UserDetailPage component
const UserDetailPage = () => {
  const { userId } = useParams(); // Access the dynamic URL parameter
  return (
    <div>
      <h2>User Detail: {userId ? userId.charAt(0).toUpperCase() + userId.slice(1) : 'Not found'}</h2>
      <p>This page displays details for user: <strong>{userId}</strong>.</p>
      {/* In a real app, you would fetch user data based on userId */}
    </div>
  );
};

const App = () => {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<h1>Home Page</h1>} />
        <Route path="dashboard" element={<DashboardLayout />}>
          <Route index element={<DashboardOverview />} />
          <Route path="profile" element={<UserProfile />} />
          <Route path="settings" element={<AppSettings />} />

          {/* Nested routes for users */}
          <Route path="users" element={<UserListPage />} /> {/* List page */}
          <Route path="users/:userId" element={<UserDetailPage />} /> {/* Dynamic detail page */}
        </Route>
        <Route path="*" element={<h2>404 Not Found</h2>} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;

