import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

// Dummy useAuth hook for demonstration
const useAuth = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  // In a real app, this would check a token, context, or Redux state
  useEffect(() => {
    // Simulate async auth check
    const token = localStorage.getItem('authToken');
    setIsAuthenticated(!!token); // Set true if token exists
  }, []);
  return { isAuthenticated };
};

function PrivateRoute({ children }) {
  const auth = useAuth(); // Your custom hook for auth state
  const navigate = useNavigate();
  const location = useLocation(); // To get current location and pass it

  useEffect(() => {
    if (!auth.isAuthenticated) {
      // Redirect to login, preserving the path user tried to access
      navigate('/login', { state: { from: location } });
    }
  }, [auth.isAuthenticated, navigate, location]);

  // Render children if authenticated, otherwise a loading spinner or null
  return auth.isAuthenticated ? children : <div>Loading...</div>; // Or a proper LoadingSpinner component
}

