import React, { useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import { createBrowserHistory } from 'history';
import { configureStore, createSlice } from '@reduxjs/toolkit';
import { Provider as ReactReduxProvider, useDispatch, useSelector } from 'react-redux';
import { 
  Routes, 
  Route, 
  unstable_HistoryRouter as HistoryRouter, 
  Link, 
  useNavigate
} from 'react-router-dom';

// --- 1. Redux Store Setup ---
// Define a dummy slice
const counterSlice = createSlice({
  name: 'counter',
  initialState: { value: 0, navigateTo: null },
  reducers: {
    increment: (state) => { state.value += 1; },
    navigateToPath: (state, action) => { state.navigateTo = action.payload; },
    clearNavigation: (state) => { state.navigateTo = null; }
  },
});

export const { increment, navigateToPath, clearNavigation } = counterSlice.actions;

// --- 2. Create history object ---
const history = createBrowserHistory();

// --- Middleware for Navigation (demonstrative) ---
const routerMiddleware = (store) => (next) => (action) => {
  if (action.type === 'counter/navigateToPath') {
    // In a real connected-react-router setup, this would be an actual nav action
    // For this example, the component will handle actual navigation via useSelector/useEffect
    console.log(`[Router Middleware] Intercepted navigation request to: ${action.payload}`);
  }
  return next(action);
};

// --- 3. Configure Redux store ---
const store = configureStore({
  reducer: {
    counter: counterSlice.reducer,
  },
  middleware: (getDefaultMiddleware) => getDefaultMiddleware().concat(routerMiddleware),
});

// --- Components ---
function Home() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const navigateTo = useSelector((state) => state.counter.navigateTo);

  // Effect to perform navigation based on Redux state
  useEffect(() => {
    if (navigateTo) {
      navigate(navigateTo); // Programmatically navigate
      dispatch(clearNavigation()); // Clear the navigation flag in Redux
    }
  }, [navigateTo, navigate, dispatch]);

  return (
    <div>
      <h2>Home Page</h2>
      <p>Counter: {useSelector(state => state.counter.value)}</p>
      <button onClick={() => dispatch(increment())}>Increment Counter</button>
      <br/><br/>
      <button onClick={() => dispatch(navigateToPath('/dashboard'))}>
        Navigate to Dashboard (via Redux Action)
      </button>
      <p><Link to="/dashboard">Go to Dashboard (via Link)</Link></p>
    </div>
  );
}

function Dashboard() {
  return (
    <div>
      <h2>Dashboard Page</h2>
      <p>You landed on the dashboard.</p>
      <p><Link to="/">Go to Home</Link></p>
    </div>
  );
}

function App() {
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/dashboard" element={<Dashboard />} />
      <Route path="*" element={<h2>404 Not Found</h2>} />
    </Routes>
  );
}

// --- 4. Render the App wrapped with Provider and HistoryRouter ---
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <ReactReduxProvider store={store}>
    <HistoryRouter history={history}>
      <App />
    </HistoryRouter>
  </ReactReduxProvider>
);

