import React from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import ReactDOM from 'react-dom/client';

// Dummy product data
const products = [
  { id: 'p1', name: 'Laptop', price: 1200 },
  { id: 'p2', name: 'Mouse', price: 25 },
  { id: 'p3', name: 'Keyboard', price: 75 },
];

// ProductList component
function ProductList() {
  const navigate = useNavigate();

  const handleViewDetails = (product) => {
    // Navigate to /product-detail, passing product data as state
    navigate('/product-detail', { state: { productData: product } });
  };

  return (
    <div>
      <h1>Our Products</h1>
      <ul>
        {products.map((product) => (
          <li key={product.id} style={{ marginBottom: '10px' }}>
            {product.name} - ${product.price}
            <button 
              onClick={() => handleViewDetails(product)}
              style={{ marginLeft: '15px', padding: '5px 10px' }}
            >
              View Details
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

// ProductDetail component
function ProductDetail() {
  const location = useLocation();
  // Retrieve product data from location.state
  const product = location.state?.productData; 
  const navigate = useNavigate();

  if (!product || !product.name || !product.price) {
    return (
      <div>
        <p>No product details found or invalid data.</p>
        <button onClick={() => navigate('/')}>Go to Product List</button>
      </div>
    );
  }

  return (
    <div>
      <h2>Product Detail</h2>
      <p>Name: <strong>{product.name}</strong></p>
      <p>Price: <strong>${product.price}</strong></p>
      <button onClick={() => navigate(-1)}>Go Back</button>
    </div>
  );
}

function App() {
  return (
    <Routes>
      <Route path="/" element={<ProductList />} />
      <Route path="/product-detail" element={<ProductDetail />} />
      <Route path="*" element={<h2>404 - Page Not Found</h2>} />
    </Routes>
  );
}

