import React from 'react';

// --- Error Boundary Component ---
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // You can also log the error to an error reporting service
    console.error('Error caught by boundary:', error, errorInfo);
    this.setState({
      error: error,
      errorInfo: errorInfo
    });
    // logErrorToMyService(error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      // You can render any custom fallback UI
      return (
        <div style={{ border: '2px solid red', padding: '20px', margin: '20px', backgroundColor: '#ffe6e6' }}>
          <h1>Oops! Something went wrong.</h1>
          <details style={{ whiteSpace: 'pre-wrap' }}>
            {this.state.error && this.state.error.toString()}
            <br />
            {this.state.errorInfo && this.state.errorInfo.componentStack}
          </details>
          <p>Please try reloading the page or contact support.</p>
        </div>
      );
    }

    return this.props.children;
  }
}

// --- Component That Might Error ---
function MyComponentThatMightError({ shouldError }) {
  if (shouldError) {
    throw new Error('I crashed! This is a simulated error.');
  }
  return <h2>This component works perfectly!</h2>;
}

// --- Usage in Parent Component ---
function App() {
  const [triggerError, setTriggerError] = React.useState(false);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      <h1>My Application</h1>
      <button onClick={() => setTriggerError(true)} style={{ marginBottom: '20px' }}>
        Trigger Error in Child Component
      </button>

      <ErrorBoundary>
        <MyComponentThatMightError shouldError={triggerError} />
      </ErrorBoundary>

      <p>This part of the application is still working.</p>
    </div>
  );
}

export default App;

