```jsx
import React from 'react';

// Component that might throw an error
function MyComponentThatMightError({ shouldThrow }) {
  if (shouldThrow) {
    throw new Error('Simulated crash!');
  }
  return <div>Successfully loaded content.</div>;
}

// Error Boundary Component implementation
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // You can also log the error to an error reporting service
    console.error('Error caught by ErrorBoundary:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      // Render any custom fallback UI
      return <h1>Component failed to load.</h1>;
    }

    return this.props.children;
  }
}

// Usage in App component
function App() {
  return (
    <div>
      <h2>Application Header</h2>
      <ErrorBoundary>
        <MyComponentThatMightError shouldThrow={true} />
      </ErrorBoundary>
      <p>This part of the app is still working.</p>
    </div>
  );
}

export default App;
```

