import React, { useState } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import './ListAnimator.css'; // Create this CSS file

function ListAnimator() {
  const [items, setItems] = useState(['Item 1', 'Item 2', 'Item 3']);
  const [newItemText, setNewItemText] = useState('');

  const handleAddItem = () => {
    if (newItemText.trim() === '') return;
    setItems(prevItems => [...prevItems, newItemText.trim()]);
    setNewItemText('');
  };

  const handleRemoveItem = (itemToRemove) => {
    setItems(prevItems => prevItems.filter(item => item !== itemToRemove));
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: 'auto' }}>
      <h2>Animated List</h2>
      <div style={{ marginBottom: '25px' }}>
        <input 
          type="text" 
          value={newItemText} 
          onChange={(e) => setNewItemText(e.target.value)}
          placeholder="New list item"
          style={{ padding: '8px', marginRight: '10px', width: '200px' }}
        />
        <button 
          onClick={handleAddItem}
          style={{ padding: '8px 15px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
        >
          Add Item
        </button>
      </div>

      {/* TODO: Wrap the list rendering with TransitionGroup and CSSTransition */}
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {items.map((item, index) => (
          <li 
            key={item} // Consider a more robust key for production, like a unique ID
            style={{
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              padding: '10px',
              border: '1px solid #eee',
              marginBottom: '5px',
              backgroundColor: '#f8f8f8',
              borderRadius: '4px'
            }}
          >
            {item}
            <button 
              onClick={() => handleRemoveItem(item)}
              style={{ padding: '5px 10px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              Remove
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ListAnimator;

