import React, { useState } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import './ListAnimator.css';

function ListAnimator() {
  const [items, setItems] = useState(['Item A', 'Item B', 'Item C'].map(text => ({ id: Math.random().toString(36).substring(7), text: text })));
  const [newItemText, setNewItemText] = useState('');

  const handleAddItem = () => {
    if (newItemText.trim() === '') return;
    const newItem = { id: Math.random().toString(36).substring(7), text: newItemText.trim() };
    setItems(prevItems => [...prevItems, newItem]);
    setNewItemText('');
  };

  const handleRemoveItem = (idToRemove) => {
    setItems(prevItems => prevItems.filter(item => item.id !== idToRemove));
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: 'auto', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center', color: '#333' }}>Animated List</h2>
      <div style={{ marginBottom: '25px', display: 'flex', justifyContent: 'center' }}>
        <input 
          type="text" 
          value={newItemText} 
          onChange={(e) => setNewItemText(e.target.value)}
          placeholder="New list item"
          style={{ 
            padding: '10px', 
            marginRight: '10px', 
            width: '200px', 
            border: '1px solid #ccc', 
            borderRadius: '4px',
            fontSize: '1em'
          }}
        />
        <button 
          onClick={handleAddItem}
          style={{ 
            padding: '10px 15px', 
            backgroundColor: '#28a745', 
            color: 'white', 
            border: 'none', 
            borderRadius: '4px', 
            cursor: 'pointer',
            fontSize: '1em'
          }}
        >
          Add Item
        </button>
      </div>

      <TransitionGroup component="ul" style={{ listStyleType: 'none', padding: 0 }}>
        {items.map((item) => (
          <CSSTransition 
            key={item.id} 
            timeout={500} 
            classNames="list-item"
          >
            <li 
              style={{
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                padding: '12px',
                border: '1px solid #eee',
                marginBottom: '8px',
                backgroundColor: '#f8f8f8',
                borderRadius: '5px',
                boxShadow: '0 1px 3px rgba(0,0,0,0.05)'
              }}
            >
              {item.text}
              <button 
                onClick={() => handleRemoveItem(item.id)}
                style={{ 
                  padding: '6px 12px', 
                  backgroundColor: '#dc3545', 
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '4px', 
                  cursor: 'pointer',
                  fontSize: '0.9em'
                }}
              >
                Remove
              </button>
            </li>
          </CSSTransition>
        ))}
      </TransitionGroup>
    </div>
  );
}

export default ListAnimator;

/* 
   Content for ListAnimator.css:

   .list-item-enter {
     opacity: 0;
     transform: translateX(-50px);
   }

   .list-item-enter-active {
     opacity: 1;
     transform: translateX(0);
     transition: opacity 500ms ease-out, transform 500ms ease-out;
   }

   .list-item-exit {
     opacity: 1;
     transform: translateX(0);
   }

   .list-item-exit-active {
     opacity: 0;
     transform: translateX(50px);
     transition: opacity 500ms ease-in, transform 500ms ease-in;
   }
*/

