import React from 'react';

// Component that intentionally throws an error
function BuggyComponent({ shouldThrow }) {
  if (shouldThrow) {
    // Simulate an error that would crash part of the UI
    throw new Error('I am a buggy component and I crashed!');
  }
  return <div style={{ border: '1px dashed lightgreen', padding: '10px', margin: '10px' }}>This is the perfectly working component content.</div>;
}

// ErrorBoundary class component implementation
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI.
    // This method is called during the render phase, so it should only update state.
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // This method is called during the commit phase (after render).
    // Use it for side effects like logging errors to a service.
    console.error('Caught by ErrorBoundary:', error, errorInfo);
    this.setState({
      error: error,
      errorInfo: errorInfo
    });
    // Example: logErrorToMyService(error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      // Check if a custom fallback component was provided
      if (this.props.fallbackComponent) {
        return this.props.fallbackComponent;
      }
      // Default fallback UI
      return (
        <div style={{ border: '2px solid red', padding: '15px', margin: '10px', backgroundColor: '#ffe6e6', borderRadius: '5px' }}>
          <h2>Oops! Something went wrong.</h2>
          <p>We're sorry for the inconvenience. Please try again later.</p>
          {/* Optional: show error details in development */}
          {process.env.NODE_ENV === 'development' && this.state.error && (
            <details style={{ whiteSpace: 'pre-wrap', marginTop: '10px' }}>
              <summary>Error Details</summary>
              {this.state.error.toString()}
              <br />
              {this.state.errorInfo && this.state.errorInfo.componentStack}
            </details>
          )}
        </div>
      );
    }

    return this.props.children;
  }
}

// Add PropTypes for better development-time validation (optional but good practice)
ErrorBoundary.propTypes = {
  children: PropTypes.node.isRequired,
  fallbackComponent: PropTypes.node // Can be a React element or component
};

// Custom fallback component example
const CustomFallback = () => (
  <div style={{ border: '2px dashed blue', padding: '15px', margin: '10px', backgroundColor: '#e6f2ff', borderRadius: '5px' }}>
    <h3>A specific section failed to load!</h3>
    <p>Please contact support with this error code: XYZ123.</p>
  </div>
);

// Application entry point for demonstration
import PropTypes from 'prop-types'; // Import PropTypes for the example

function App() {
  const [triggerErrorDefault, setTriggerErrorDefault] = React.useState(false);
  const [triggerErrorCustom, setTriggerErrorCustom] = React.useState(false);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      <h1>My Application</h1>

      <hr />
      <h2>Section 1: Default Error Boundary</h2>
      <button onClick={() => setTriggerErrorDefault(true)} style={{ marginBottom: '15px' }}>
        Trigger Default Error
      </button>
      <ErrorBoundary>
        <BuggyComponent shouldThrow={triggerErrorDefault} />
      </ErrorBoundary>

      <hr />
      <h2>Section 2: Custom Fallback Component</h2>
      <button onClick={() => setTriggerErrorCustom(true)} style={{ marginBottom: '15px' }}>
        Trigger Custom Fallback Error
      </button>
      <ErrorBoundary fallbackComponent={<CustomFallback />}>
        <BuggyComponent shouldThrow={triggerErrorCustom} />
      </ErrorBoundary>

      <hr />
      <p>This part of the application remains functional and visible.</p>
    </div>
  );
}

export default App;

