import React from 'react';

function ErrorFallback({ error, resetErrorBoundary }) {
  return (
    <div role="alert" style={{ padding: '20px', border: '1px solid orange', color: 'orange' }}>
      <p>Something went wrong (via `react-error-boundary`):</p>
      <pre>{error.message}</pre>
      <button onClick={resetErrorBoundary}>Try again</button>
    </div>
  );
}

function ComponentThatMightThrow() {
  // Simulate an error for demonstration
  const [shouldThrow, setShouldThrow] = React.useState(false);

  if (shouldThrow) {
    throw new Error('I crashed!');
  }

  return (
    <div>
      <p>This is a component that might throw an error.</p>
      <button onClick={() => setShouldThrow(true)}>Trigger Error</button>
    </div>
  );
}

function AppWithErrorBoundary() {
  return (
    <ErrorBoundary 
      FallbackComponent={ErrorFallback}
      onReset={() => {
        // Reset the state of your app here, e.g., navigate home or clear data
        console.log('Error boundary reset!');
      }}
      resetKeys={['someKey']} // Changing this key would reset the boundary
    >
      <ComponentThatMightThrow />
    </ErrorBoundary>
  );
}

export default AppWithErrorBoundary;

import React, { useState } from 'react';

function ControlledForm() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
