          <h2>Oops! Something went wrong in this section.</h2>
          <p>Please try again or contact support.</p>
          <details style={{ whiteSpace: 'pre-wrap', fontSize: '0.8em' }}>
            {this.state.error && this.state.error.toString()}
            <br />
            {this.state.errorInfo && this.state.errorInfo.componentStack}
          </details>
        </div>
      );
    }

    return this.props.children;
  }
}

function App() {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      <h1>React Error Boundary Example</h1>
      <p>This part of the app should remain functional even if a child component crashes.</p>
      <MyErrorBoundary>
        <BuggyComponent />
      </MyErrorBoundary>
      <p>This text is outside the error boundary and demonstrates that the rest of the application remains mounted.</p>
      <hr/>
      <h2>Another section (not handled by the first boundary)</h2>
      <div>This content should always be visible.</div>
    </div>
  );
}

export default App;

