import React, { useState } from 'react';
import { ErrorBoundary } from 'react-error-boundary';

function BuggyFunctionalComponent() {
  const [count, setCount] = useState(0);

  const handleClick = () => {
    // Simulate an error if count reaches 3
    if (count === 2) {
      throw new Error('Crash! Count reached critical level.');
    }
    setCount(prevCount => prevCount + 1);
  };

  return (
    <div style={{ border: '1px dashed lightblue', padding: '10px', marginBottom: '20px' }}>
      <p>Current count: {count}</p>
      <button onClick={handleClick}>Increment Count</button>
      <p>Clicking 3 times will cause an error.</p>
    </div>
  );
}

function ErrorFallback({ error, resetErrorBoundary }) {
  return (
    <div role="alert" style={{ border: '2px solid orange', padding: '20px', backgroundColor: '#ffe', color: '#b60' }}>
      <h2>Something went wrong in this component:</h2>
      <pre style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word', color: '#d9534f' }}>{error.message}</pre>
      <button onClick={resetErrorBoundary} style={{ padding: '8px 15px', backgroundColor: '#f0ad4e', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', marginTop: '10px' }}>
        Try again
      </button>
    </div>
  );
}

function App() {
  const [showBuggyComponent, setShowBuggyComponent] = useState(true);

  const handleResetError = () => {
    // This function is called when the 'Try again' button in ErrorFallback is clicked.
    // Here, we reset the state that might have caused the error, or re-mount the component.
    console.log('Error boundary reset initiated.');
    setShowBuggyComponent(false); // Unmount current buggy component instance
    setTimeout(() => setShowBuggyComponent(true), 0); // Re-mount a new instance
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      <h1>Using `react-error-boundary`</h1>
      <ErrorBoundary 
        FallbackComponent={ErrorFallback}
        onReset={handleResetError}
        // Adding resetKeys prop is another way to imperatively reset the boundary
        // resetKeys={[showBuggyComponent]}
      >
        {showBuggyComponent ? <BuggyFunctionalComponent /> : null}
      </ErrorBoundary>
      <p>This text is outside the error boundary and demonstrates that the rest of the application remains mounted.</p>
    </div>
  );
}

export default App;

