      <div style={{ marginBottom: '15px' }}>
        <label htmlFor="name" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Name:</label>
        <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} 
               style={{ width: '100%', padding: '10px', border: errors.name ? '1px solid red' : '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }} />
        {errors.name && <p style={{ color: 'red', fontSize: '0.85em', marginTop: '5px' }}>{errors.name}</p>}
      </div>

      <div style={{ marginBottom: '15px' }}>
        <label htmlFor="email" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Email:</label>
        <input type="email" id="email" name="email" value={formData.email} onChange={handleChange}
               style={{ width: '100%', padding: '10px', border: errors.email ? '1px solid red' : '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }} />
        {errors.email && <p style={{ color: 'red', fontSize: '0.85em', marginTop: '5px' }}>{errors.email}</p>}
      </div>

      <div style={{ marginBottom: '15px' }}>
        <label htmlFor="subject" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Subject:</label>
        <select id="subject" name="subject" value={formData.subject} onChange={handleChange}
                style={{ width: '100%', padding: '10px', border: errors.subject ? '1px solid red' : '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }}>
          <option value="General Inquiry">General Inquiry</option>
          <option value="Feature Request">Feature Request</option>
          <option value="Bug Report">Bug Report</option>
          <option value="Other">Other</option>
        </select>
        {errors.subject && <p style={{ color: 'red', fontSize: '0.85em', marginTop: '5px' }}>{errors.subject}</p>}
      </div>

      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="message" style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Message:</label>
        <textarea id="message" name="message" value={formData.message} onChange={handleChange} rows="5"
                  style={{ width: '100%', padding: '10px', border: errors.message ? '1px solid red' : '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box', resize: 'vertical' }}></textarea>
        {errors.message && <p style={{ color: 'red', fontSize: '0.85em', marginTop: '5px' }}>{errors.message}</p>}
      </div>

      <button type="submit" style={{ width: '100%', padding: '12px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', fontSize: '1em', cursor: 'pointer', transition: 'background-color 0.3s ease' }}>
        Submit Feedback
      </button>
      {submissionMessage && <p style={{ textAlign: 'center', marginTop: '15px', color: submissionMessage.includes('Thank') ? 'green' : 'red' }}>{submissionMessage}</p>}
    </form>
  );
}

export default FeedbackForm;

